import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const MovieContext = createContext();

function Provider({ children }) {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]);

  const API_KEY = "977df55b389183b892b6c11af75af168";
  const API_URL = "https://api.themoviedb.org/3/";

  const getMovies = async (endpoint) => {
    const response = await axios.get(
      `${API_URL}movie/${endpoint}?api_key=${API_KEY}`
    );
    return response.data.results;
  };

  const findMovie = async (movieId) => {
    const response = await axios.get(
      `${API_URL}movie/${movieId}?api_key=${API_KEY}`
    );
    return response.data;
  };

  const test = async (movieId) => {
    const response = await axios.get(
      `${API_URL}movie/${movieId}/keywords?api_key=${API_KEY}`
    );
    console.log(response)
    //return response.data;
  };

  const getGenres = async () => {
    const response = await axios.get(
      `${API_URL}genre/movie/list?api_key=${API_KEY}`
    );
    setGenres(response.data.genres);
  };

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await getMovies("popular");
      setMovies(data);
    };

    fetchMovies();
    getGenres();
  }, []);

  const sharedValuesAndMethods = {
    movies,
    genres,
    getMovies,
    findMovie,
    test
  };

  return (
    <MovieContext.Provider value={sharedValuesAndMethods}>
      {children}
    </MovieContext.Provider>
  );
}

const useMovieContext = () => useContext(MovieContext);
export { Provider, useMovieContext };
export default MovieContext;
