import React from "react";
import { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";

function Movies() {
  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top_rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];

  const [movieType, setMovieType] = useState(movieTypes[0]);

  const change = (type) => {
    setMovieType(type);
  };

  return (
    <div className="all-movies">
      <div className="filter">
        {movieTypes.map((type) => (
          <button
            key={type.endpoint}
            type="button"
            onClick={() => change(type)}
          >
            {type.title}
          </button>
        ))}
      </div>
      <div className="container">
        <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
      </div>
    </div>
  );
}

export default Movies;
