import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { FreeMode, Autoplay, Navigation } from "swiper";
import { useMovieContext } from "../../context/MovieContext";
import "./movies-slide.scss";

function MoviesSlide() {
  const { movies } = useMovieContext();

  return (
    <section className="section">
        <h2 className="section-title"><i className="bi bi-collection-play"></i> Popular Movies</h2>
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        loop={true}
        slidesPerView={1.5}
        breakpoints={{
          400: {
            slidesPerView: 2,
            centeredSlides: false,
          },
          545: {
            slidesPerView: 2.5,
            centeredSlides: false,
          },
          645: {
            slidesPerView: 3,
            centeredSlides: false,
          },
          768: {
            slidesPerView: 3.5,
            centeredSlides: false,
          },
          868: {
            slidesPerView: 4,
            centeredSlides: false,
          },
          1100: {
            slidesPerView: 5,
            centeredSlides: false,
          },
          1200: {
            slidesPerView: 6,
            centeredSlides: false,
          },
        }}
        freeMode={true}
        autoplay={{
          delay: 2500,
        }}
        navigation={true}
        modules={[FreeMode, Autoplay, Navigation]}
      >
        {movies.map((movie) => (
          <SwiperSlide key={movie.id}>
            <a href="s">
              <img
                className="movie-poster-img"
                src={"https://image.tmdb.org/t/p/original" + movie.poster_path}
                alt={movie.title}
              />
              <div className="movie-infos">
                <h2 className="swiper-title">{movie.title}</h2>
                <div className="swiper-vote">
                  <span className="imdb">IMDb</span>
                  <span className="vote">{movie.vote_average}</span>
                </div>
                <div className="swiper-overview">
                  <p>{movie.overview}</p>
                </div>
              </div>
            </a>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}

export default MoviesSlide;
