import React, { useState } from "react";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useMovieContext } from "../../context/MovieContext";
import "./movie-detail.scss";
import ReactPlayer from "react-player";
import MoviesSlide from "../movies-slide/MoviesSlide";
function MovieDetail() {
  const { findMovie, getMovieDetails, getMovieActors } = useMovieContext();
  const [movie, setMovie] = useState();
  const [videos, setVideos] = useState([]);
  const [actors, setActors] = useState([]);
  const { movieId } = useParams();

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await findMovie(movieId);
      setMovie(data);
    };

    const fetchMovieTrailers = async () => {
      const data = await getMovieDetails(movieId, "videos");
      setVideos(data);
    };

    const fetchMovieActors = async () => {
      const data = await getMovieActors(movieId);
      setActors(data);
    };

    fetchMovies();
    fetchMovieTrailers();
    fetchMovieActors();
  }, [movieId, findMovie, getMovieDetails, getMovieActors]);

  console.log(actors);

  return (
    <div className="movie-detail-container">
      {movie && videos ? (
        <>
          <div
            className="main-img"
            style={{
              backgroundImage:
                "url(https://image.tmdb.org/t/p/original" + movie.backdrop_path,
            }}
          />
          <div className="container">
            <div className="movie-detail">
              <div className="movie-grid main-hero">
                <img
                  src={"https://image.tmdb.org/t/p/w500" + movie.poster_path}
                  alt="ss"
                  className="movie-poster"
                />
                <div className="hero-infos">
                  <h2 className="hero-title">{movie.title}</h2>
                  <div className="hero-genres">
                    {movie.genres.map((genre) => (
                      <div key={genre.id} className="genre-box">
                        <i className="bi bi-record"></i>
                        {genre.name}
                      </div>
                    ))}
                  </div>
                  <div className="hero-vote">
                    <span className="imdb">IMDb</span>
                    <span className="vote">
                      {movie.vote_average.toFixed(1)} ({movie.vote_count})
                    </span>
                  </div>
                  <span className="date">
                    <i className="bi bi-calendar2-date"></i>{" "}
                    {movie.release_date}
                  </span>
                  <div className="hero-overview">
                    <p>{movie.overview}</p>
                  </div>
                  {movie.tagline ? (
                    <i className="tagline">
                      <i className="bi bi-quote"></i> {movie.tagline}
                    </i>
                  ) : null}
                  <a className="hero-link" href="#trailer">
                    <i className="bi bi-collection-play"></i> Watch Now
                    <i className="bi bi-arrow-right arrow"></i>
                  </a>
                </div>
              </div>
            </div>

            <div className="actors">
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />

<img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
                <img
                  src="https://image.tmdb.org/t/p/original/9JBEPLTPSm0d1mbEcLxULjJq9Eh.jpg"
                  alt="ss"
                />
            </div>

            {videos.map((video) =>
              video.type === "Trailer" ? (
                <section key={video.id} id="trailer" className="section">
                  <div className="section-title">
                    <h2 className="title">
                      <i className="bi bi-play-btn"></i> Trailer
                    </h2>
                    <div className="line"></div>
                  </div>
                  <ReactPlayer
                    className="trailer"
                    url={`https://www.youtube.com/watch?v=${video.key}`}
                  />
                </section>
              ) : null
            )}
          </div>
          <MoviesSlide
            movieId={movie.id}
            endpoint={"recommendations"}
            title={"Recommendation"}
          />
          <MoviesSlide
            movieId={movie.id}
            endpoint={"similar"}
            title={"Similar"}
          />
        </>
      ) : (
        "***** ERROR PAGE *****"
      )}
    </div>
  );
}

export default MovieDetail;
