import React, { useState } from "react";
import { useMovieContext } from "../../context/MovieContext";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";
import axios from "axios";

function Movies({ type }) {

  const {searchMovie} = useMovieContext()

  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);

  const searchMovies = async () => {
    const url = `https://api.themoviedb.org/3/search/movie?api_key=977df55b389183b892b6c11af75af168&language=en-US&query=${query}&page=1&include_adult=false`;

    try {
      const res = await axios.get(url);
      setMovies(res.data.results);
    } catch (err) {
      console.error(err);
    }
  };

  const [search, setSearch] = useState();
  const [inputClass, setInputClass] = useState("");

  const searchPressed = (e) => {
    searchMovie(search)
  };

  const types = {
    movie: [
      {
        title: "Popular",
        endpoint: "popular",
      },
      {
        title: "Top Rated",
        endpoint: "top_rated",
      },
      {
        title: "Upcoming",
        endpoint: "upcoming",
      },
    ],
    tv: [
      {
        title: "Popular",
        endpoint: "popular",
      },
      {
        title: "Top Rated",
        endpoint: "top_rated",
      },
      {
        title: "Airing Today",
        endpoint: "airing_today",
      },
      {
        title: "On The Air",
        endpoint: "on_the_air",
      },
    ],
  };

  const [selectedType, setSelectedType] = useState(types[type][0]);

  const changeType = (objType) => setSelectedType(objType);

  return (
    <>
      <div className="all-movies">
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <input
          className={inputClass}
          type="text"
          name="search"
          value={search}
          placeholder="search.."
          onKeyPress={searchPressed}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="container">
          <div className="filter">
            <div className="filter-buttons">
              {types[type].map((type) => (
                <button
                  className={
                    selectedType.endpoint === type.endpoint
                      ? "filter-btn active"
                      : "filter-btn"
                  }
                  key={type.endpoint}
                  type="button"
                  onClick={() => changeType(type)}
                >
                  {type.title}
                </button>
              ))}
            </div>
          </div>
          <MoviesBoxes
            type={type}
            title={selectedType.title}
            endpoint={selectedType.endpoint}
          />
        </div>
      </div>
    </>
  );
}

export default Movies;
