import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const MovieContext = createContext();

function Provider({ children }) {
  const [movies, setMovies] = useState([]);

  const API_KEY = "977df55b389183b892b6c11af75af168";
  const API_URL = "https://api.themoviedb.org/3/movie/popular";
  const fetchPopularMovies = async () => {
    const response = await axios.get(`${API_URL}?api_key=${API_KEY}`);
    return response.data.results;
  };

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await fetchPopularMovies();
      setMovies(data);
    };

    fetchMovies();
  }, []);

  console.log(movies);
  const sharedValuesAndMethods = {};

  return (
    <MovieContext.Provider value={sharedValuesAndMethods}>
      {children}
    </MovieContext.Provider>
  );
}

const useMovieContext = () => useContext(MovieContext);
export { Provider, useMovieContext };
export default MovieContext;
