import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { useMovieContext } from "../../context/MovieContext";

function MoviesBoxes({ endpoint, title }) {
  const { getTv, getMovieDetails } = useMovieContext();
  const [tvData, setTvData] = useState([]);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await getTv(endpoint);
      setTvData(data);
      setLoading(false);
    };

    fetchMovies();
  }, [endpoint, getTv, getMovieDetails]);

  return (
    <>
      {isLoading ? (
        <div className="loader-container">
          <div className="loader"></div>
        </div>
      ) : tvData.length > 0 ? (
        <section className="section">
          <div className="section-title">
            <h2 className="title">
              <i className="bi bi-collection-play"></i> {title} Movies
            </h2>
            <div className="line"></div>
          </div>
          <div className="movie-boxes">
            {tvData.map((movie) => (
              <div key={movie.id} className="movie-box">
                <Link to={"/movie/" + movie.id}>
                  <img
                    className="movie-box-img"
                    src={
                      "https://image.tmdb.org/t/p/original" + movie.poster_path
                    }
                    alt={"s"}
                  />
                  <div className="movie-infos">
                    <h2 className="movie-title">{movie.title}</h2>
                    <div className="movie-vote">
                      <span className="imdb">IMDb</span>
                      <span className="vote">{movie.vote_average}</span>
                    </div>
                    <div className="movie-overview">
                      <p>{movie.overview}</p>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        </section>
      ) : null}
    </>
  );
}

export default MoviesBoxes;
