import React, { useState } from "react";
import AnimatedPage from "../AnimatedPage";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";
import { motion, useIsPresent} from "framer-motion";

function Movies() {
  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top_rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];
  const isPresent = useIsPresent();

  const [movieType, setMovieType] = useState(movieTypes[0]);

  const changeType = (type) => setMovieType(type);

  return (
    <div className="all-movies">
      <div className="container">
        <div className="filter">
          <div className="filter-buttons">
            {movieTypes.map((type) => (
              <button
                className={
                  movieType.endpoint === type.endpoint
                    ? "filter-btn active"
                    : "filter-btn"
                }
                key={type.endpoint}
                type="button"
                onClick={() => changeType(type)}
              >
                {type.title}
              </button>
            ))}
          </div>
        </div>
        <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
      </div>
      <motion.div
        initial={{ scaleX: 1 }}
        animate={{ scaleX: 0, transition: { duration: 0.5, ease: "circOut" } }}
        exit={{ scaleX: 1, transition: { duration: 0.5, ease: "circIn" } }}
        style={{ originX: isPresent ? 0 : 1 }}
        className="privacy-screen"
      />
    </div>
  );
}

export default Movies;
