import React from "react";
import { useMovieContext } from "../../context/MovieContext";
import "./main-slide.scss";

function MainSlide() {
  const { movies, genres } = useMovieContext();
  const randomIndex = Math.floor(Math.random() * 20);
  return (
    <div className="main-slide-container">
      {movies.length > 0 ? (
        <div className="main-slide">
          <img
            className="slide-img"
            src={
              "https://image.tmdb.org/t/p/original" +
              movies[randomIndex].backdrop_path
            }
            alt="movie"
          />
          <div className="container">
            <div className="slide-infos">
              <h2 className="slide-title truncate">
                {movies[randomIndex].title}
              </h2>
              <div className="slide-genres">
                {movies[randomIndex].genres.ids.length > 0
                  ? movies[randomIndex].genres_ids.map((id) => (
                      <div key={id} className="genre-box">
                        <i className="bi bi-record"></i>{" "}
                        {genres.find((genre) => genre.id === id).name}
                      </div>
                    ))
                  : null}
              </div>
              <div className="slide-vote">
                <span className="imdb">IMDb</span>
                <span className="vote">{movies[randomIndex].vote_average}</span>
              </div>
              <div className="slide-overview">
                <p>{movies[randomIndex].overview}</p>
              </div>
              <a className="slide-link" href={movies[randomIndex].id}>
                <i className="bi bi-collection-play"></i> Watch Now
                <i className="bi bi-arrow-right arrow"></i>
              </a>
            </div>
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default MainSlide;
