import React, { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";

function Movies() {
  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top_rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];

  const [movieType, setMovieType] = useState(movieTypes[0]);

  const changeType = (type) => setMovieType(type);

  return (
    <>
      <div className="all-movies">
        <div className="container">
          <div className="filter">
            <div className="filter-buttons">
              {movieTypes.map((type) => (
                <button
                  className={
                    movieType.endpoint === type.endpoint
                      ? "filter-btn active"
                      : "filter-btn"
                  }
                  key={type.endpoint}
                  type="button"
                  onClick={() => changeType(type)}
                >
                  {type.title}
                </button>
              ))}
            </div>
          </div>
          <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
        </div>
      </div>
      <AnimatedPage />
    </>
  );
}

export default Movies;
