import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useMovieContext } from "../../context/MovieContext";
import "./search.scss";

function Search() {
  const { searchMovie } = useMovieContext();

  const [query, setQuery] = useState("");
  const [moviesData, setMoviesData] = useState([]);

  const handleInputChange = (event) => {
    setQuery(event.target.value);
    if (event.target.value) {
      const search = async () => {
        const data = await searchMovie("movie", event.target.value);
        setMoviesData(data);
      };

      search();
    } else {
      setMoviesData([]);
    }
  };

  console.log(moviesData);
  return (
    <div className="search-container">
      <div className="container">
        <section className="section">
          <div className="section-title">
            <h2 className="title">
              <i className="bi bi-search"></i> Search Movie
            </h2>
            <div className="line"></div>
          </div>
          <input
            className="search-input"
            type="text"
            value={query}
            onChange={handleInputChange}
          />
          <div className="movie-boxes">
            {moviesData.length > 0 && query.length !== 0
              ? moviesData.map((movie) =>
                  movie.poster_path ? (
                    <div key={movie.id} className="movie-box">
                      <Link to={`/movie/${movie.id}`}>
                        <img
                          className="movie-box-img"
                          src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
                          alt={movie.title}
                        />
                        <div className="movie-infos">
                          <h2 className="movie-title">{movie.title}</h2>
                          <div className="movie-vote">
                            <span className="imdb">IMDb</span>
                            <span className="vote">{movie.vote_average}</span>
                          </div>
                          <div className="movie-overview">
                            <p>{movie.overview}</p>
                          </div>
                        </div>
                      </Link>
                    </div>
                  ) : null
                )
              : "null"}
          </div>
        </section>
      </div>
    </div>
  );
}

export default Search;
