import React from "react";
import { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";
import { motion, useIsPresent, useScroll, useSpring } from "framer-motion";

function Movies() {

  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });
  const isPresent = useIsPresent();

  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top_rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];

  const [movieType, setMovieType] = useState(movieTypes[0]);

  const change = (type) => setMovieType(type);

  return (
    <div className="all-movies">
      <div className="container">
        <div className="filter">
          <div className="filter-buttons">
            {movieTypes.map((type) => (
              <button
                className={
                  movieType.endpoint === type.endpoint
                    ? "filter-btn active"
                    : "filter-btn"
                }
                key={type.endpoint}
                type="button"
                onClick={() => change(type)}
              >
                {type.title}
              </button>
            ))}
          </div>
        </div>
        <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
      </div>
    </div>
  );
}

export default Movies;
