import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { useMovieContext } from "../../context/MovieContext";
import "./tv-hero.scss";

function TvHero() {
  const { getMovies } = useMovieContext();
  const [serious, setSerious] = useState([])

  const randomIndex = Math.floor(Math.random() * 20);

  useEffect(() => {
    const fetchSerious = () => {
        const data = getMovies("tv","popular");
        setSerious(data);
    }

    fetchSerious()
  })

  return (
    <div className="tv-hero-area">
      <img
        className="hero-img"
        src={
          "https://image.tmdb.org/t/p/original" +
          serious[randomIndex].backdrop_path
        }
        alt={serious[randomIndex].title}
      />
      <div className="tv-hero">
        <div className="main-hero">
          <div className="container">
            <div className="hero-infos">
              <h2 className="hero-title">{serious[randomIndex].title}</h2>
              <div className="hero-overview">
                <p>{serious[randomIndex].overview}</p>
              </div>
              <a className="hero-link" href={"movie/" + serious[randomIndex].id}>
                <i className="bi bi-collection-play"></i> Watch Now
                <i className="bi bi-arrow-right arrow"></i>
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TvHero;
