import React from "react";
import { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";
import { motion } from "framer-motion";

const animations = {
  initial: {opacity: 0, x: 100},
  animate:{opacity: 1, x: 0}, 
  exit:{opacity: 0, x: -100}, 
}

function Movies() {
  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top_rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];

  const [movieType, setMovieType] = useState(movieTypes[0]);

  const change = (type) => setMovieType(type);

  return (
    <motion.div>
      <div className="all-movies">
        <div className="container">
          <div className="filter">
            <div className="filter-buttons">
              {movieTypes.map((type) => (
                <button
                  className={
                    movieType.endpoint === type.endpoint
                      ? "filter-btn active"
                      : "filter-btn"
                  }
                  key={type.endpoint}
                  type="button"
                  onClick={() => change(type)}
                >
                  {type.title}
                </button>
              ))}
            </div>
          </div>
          <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
        </div>
      </div>
    </motion.div>
  );
}

export default Movies;
