import React, { useState } from "react";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import MovieContext, { useMovieContext } from "../../context/MovieContext";

function MovieDetail() {
  const { movies, genres, findMovie } = useMovieContext();
  const [movie, setMovie] = useState();
  const { movieId } = useParams();

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await findMovie(movieId);
      setMovie(data);
    };

    fetchMovies();
  }, [movieId, findMovie]);

  console.log(MovieContext)

  return (
    <div className="movie-detail-container">
      {movie && genres.length > 0 ? (
        <>
          <img
            className="main-img"
            src="https://image.tmdb.org/t/p/original/3CxUndGhUcZdt1Zggjdb2HkLLQX.jpg"
            alt={"movie.title"}
          />
          <div className="container">
            <div className="movie-grid">
              <img
                src="https://image.tmdb.org/t/p/w500/ngl2FKBlU4fhbdsrtdom9LVLBXw.jpg"
                alt="ss"
                className="movie-poster"
              />
              <div className="hero-infos">
                <h2 className="hero-title">{movie.title}</h2>
                <div className="hero-genres">
                  {movie.genre_ids.map((id) => (
                    <div key={id} className="genre-box">
                      <i className="bi bi-record"></i>
                      {genres.find((genre) => genre.id === id).name}
                    </div>
                  ))}
                </div>
                <div className="hero-vote">
                  <span className="imdb">IMDb</span>
                  <span className="vote">{movie.vote_average}</span>
                </div>
                <div className="hero-overview">
                  <p>{movie.overview}</p>
                </div>
                <a className="hero-link" href={movie.id}>
                  <i className="bi bi-collection-play"></i> Watch Now
                  <i className="bi bi-arrow-right arrow"></i>
                </a>
              </div>
            </div>
          </div>
        </>
      ) : (
        "a"
      )}
    </div>
  );
}

export default MovieDetail;
