import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const MovieContext = createContext();

function Provider({ children }) {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]);

  const API_KEY = "977df55b389183b892b6c11af75af168";
  const API_URL = "https://api.themoviedb.org/3/";

  const movieType = {
    upcoming: "upcoming",
    popular: "popular",
    top_rated: "top_rated",
  };

  const fetchMovies = async () => {
    const response = await axios.get(
      `${API_URL}movie/popular?api_key=${API_KEY}`
    );
    return response.data.results;
  };

  const fetchGenres = async () => {
    const response = await axios.get(
      `${API_URL}genre/movie/list?api_key=${API_KEY}`
    );
    setGenres(response.data.genres);
  };

  useEffect(() => {
    setMovies(fetchMovies("movie/popular"));
    fetchGenres();
  }, []);

  const sharedValuesAndMethods = {
    movies,
    genres,
  };

  return (
    <MovieContext.Provider value={sharedValuesAndMethods}>
      {children}
    </MovieContext.Provider>
  );
}

const useMovieContext = () => useContext(MovieContext);
export { Provider, useMovieContext };
export default MovieContext;
