import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { FreeMode, Autoplay, Navigation } from "swiper";
import { useMovieContext } from "../../context/MovieContext";
import "./movies-slide.scss";

function MoviesSlide() {
  const { movies, genres } = useMovieContext();

  return (
    <Swiper
      spaceBetween={30}
      centeredSlides={true}
      loop={true}
      slidesPerView={1.5}
      breakpoints={{
        400: {
            slidesPerView: 2,
            centeredSlides: false,
          },
        545: {
            slidesPerView: 2.5,
            centeredSlides: false,
          },
        645: {
          slidesPerView: 3,
          centeredSlides: false,
        },
        768: {
          slidesPerView: 3.5,
          centeredSlides: false,
        },
        868: {
          slidesPerView: 4,
          centeredSlides: false,
        },
        1100: {
          slidesPerView: 5,
          centeredSlides: false,
        },
        1200: {
          slidesPerView: 6,
          centeredSlides: false,
        },
      }}
      freeMode={true}
      autoplay={{
        delay: 2500,
      }}
      navigation={true}
      modules={[FreeMode, Autoplay, Navigation]}
    >
      {movies.map((movie) => (
        <SwiperSlide key={movie.id}>
          <img
            className="movie-poster-img"
            src={"https://image.tmdb.org/t/p/original" + movie.poster_path}
            alt={movie.title}
          />
          <div className="swiper-slide movie-infos">
          <div className="slide-infos">
              <h2 className="slide-title">{movie.title}</h2>
              <div className="slide-genres">
                {movie.genre_ids.map((id) => (
                  <div key={id} className="genre-box">
                    <i className="bi bi-record"></i>
                    {genres.find((genre) => genre.id === id).name}
                  </div>
                ))}
              </div>
              <div className="slide-vote">
                <span className="imdb">IMDb</span>
                <span className="vote">{movie.vote_average}</span>
              </div>
              <div className="slide-overview">
                <p>{movie.overview}</p>
              </div>
              <a className="slide-link" href={movie.id}>
                <i className="bi bi-collection-play"></i> Watch Now
                <i className="bi bi-arrow-right arrow"></i>
              </a>
            </div>
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MoviesSlide;
