import React, { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";
import axios from "axios";

function Movies({ type }) {

  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);

  const handleInputChange = (event) => {
    setQuery(event.target.value);
    if (event.target.value) {
      axios
        .get(
          `https://api.themoviedb.org/3/search/movie?api_key=977df55b389183b892b6c11af75af168&query=${event.target.value}`
        )
        .then((response) => setMovies(response.data.results))
        .catch((error) => console.log(error));
    } else {
      setMovies([]);
    }
  };

  const types = {
    movie: [
      {
        title: "Popular",
        endpoint: "popular",
      },
      {
        title: "Top Rated",
        endpoint: "top_rated",
      },
      {
        title: "Upcoming",
        endpoint: "upcoming",
      },
    ],
    tv: [
      {
        title: "Popular",
        endpoint: "popular",
      },
      {
        title: "Top Rated",
        endpoint: "top_rated",
      },
      {
        title: "Airing Today",
        endpoint: "airing_today",
      },
      {
        title: "On The Air",
        endpoint: "on_the_air",
      },
    ],
  };

  const [selectedType, setSelectedType] = useState(types[type][0]);

  const changeType = (objType) => setSelectedType(objType);

  return (
    <>
      <div className="all-movies">
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      <input type="text" value={query} onChange={handleInputChange} />
      <ul>
        {movies.map((movie) => (
          <li key={movie.id}>{movie.title}</li>
        ))}
      </ul>
        <div className="container">
          <div className="filter">
            <div className="filter-buttons">
              {types[type].map((type) => (
                <button
                  className={
                    selectedType.endpoint === type.endpoint
                      ? "filter-btn active"
                      : "filter-btn"
                  }
                  key={type.endpoint}
                  type="button"
                  onClick={() => changeType(type)}
                >
                  {type.title}
                </button>
              ))}
            </div>
          </div>
          <MoviesBoxes
            type={type}
            title={selectedType.title}
            endpoint={selectedType.endpoint}
          />
        </div>
      </div>
    </>
  );
}

export default Movies;
