import React, { useState, useEffect } from "react";
import axios from "axios";

const Table = () => {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]);

  const fetchMovies = async () => {
    const response = await axios.get(
      "https://api.themoviedb.org/3/movie/popular?api_key=977df55b389183b892b6c11af75af168"
    );
    setMovies(response.data.results);
  };

  const fetchGenres = async () => {
    const gresponse = await axios.get(
      "https://api.themoviedb.org/3/genre/movie/list?api_key=977df55b389183b892b6c11af75af168"
    );
    setGenres(gresponse.data.genres);
  };

  useEffect(() => {
    fetchMovies();
    fetchGenres();
  }, []);

  return (
    <div style={{ backgroundColor: "white" ,  marginTop: "300px"}}>
      <h1>Movie Table</h1>
      <ul>
        {genres.map((genre) => (
          <li key={genre.id}>{genre.name}</li>
        ))}
      </ul>
      <table>
        <thead>
          <tr>
            <th>Backdrop</th>
            <th>Poster</th>
            <th>Title</th>
            <th>Genres</th>
            <th>Release Date</th>
            <th>Vote Average</th>
            <th>Overview</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>
                <img
                  width={100}
                  src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                  alt={`${movie.title} poster`}
                />
              </td>
              <td>
                <img
                  width={100}
                  src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
                  alt={`${movie.title} poster`}
                />
              </td>
              <td>{movie.title}</td>
              <td>{movie.genre_ids.map((gid) => <span key={gid} style={{display: "block", fontSize: "10px"}}>{genres.find(genre => genre.id === gid).name}</span>)}</td>
              <td>{movie.release_date}</td>
              <td>{movie.vote_average}</td>
              <td>{movie.overview}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
