import React from "react";
import Logo from "../../assets/logo.svg";
import { Link, NavLink } from "react-router-dom";
import "./header.scss";
import { useState } from "react";

function Header() {
  const [navCheck, setNavCheck] = useState(false);
  const navLinks = [
    {
      display: "Home",
      path: "/",
      icon: "house",
    },
    {
      display: "Movies",
      path: "/movies",
      icon: "collection-play",
    },
    {
      display: "TV Series",
      path: "/tv",
      icon: "play",
    },
  ];

  const handleNavbar = () => {
    const colorPicker = document.querySelector(".navbar");
    colorPicker.classList.add("active");
    console.log(colorPicker)
    document.addEventListener("click", e => {
        if(e.target.className !== "clickLabel") {
            colorPicker.classList.remove("active");
        }
    });
  };

  function showMenu(clickLabel) {
    const colorPicker = document.querySelector(".color-picker");
    colorPicker.classList.add("show");
    document.addEventListener("click", e => {
        if(e.target != clickLabel) {
            colorPicker.classList.remove("show");
        }
    });
}

  return (
    <header className="header">
      <button onClick={() => showMenu(this)} type="button" className="menu-btn">
        <i className="bi bi-list"></i>
      </button>
      <Link to="/">
        <img className="logo" src={Logo} alt="logo" />
      </Link>
      <navbar className="navbar">
        <Link to="/">
          <img className="logo nav-logo" src={Logo} alt="logo" />
        </Link>
        {navLinks.map((link) => (
          <NavLink key={link.path} className="nav-link" to={link.path}>
            <i className={"bi bi-" + link.icon}></i>
            {link.display}
          </NavLink>
        ))}
      </navbar>
      <Link to="" className="register-link">
        <i className="bi bi-box-arrow-in-right"></i> Sign In
      </Link>
    </header>
  );
}

export default Header;
