import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { useMovieContext } from "../../context/MovieContext";
import "./movies-boxes.scss";

function MoviesBoxes({ movieId, endpoint, title }) {
  const { getMovies, getMovieDetails } = useMovieContext();
  const [movieData, setMovieData] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const data = await getMovies(endpoint);
      setMovieData(data);
    };

    const fetchMovieDetails = async () => {
      const data = await getMovieDetails(movieId, endpoint);
      setMovieData(data);
    };

    movieId ? fetchMovieDetails() : fetchMovies();
  }, [endpoint, getMovies, movieId, getMovieDetails]);

  return (
    <>
      {movieData.length > 0 ? (
        <section className="section">
          <div className="section-title">
            <h2 className="title">
              <i className="bi bi-collection-play"></i> {title} Movies
            </h2>
            <div className="line"></div>
          </div>
          <div className="movie-boxes">
            {movieData.map((movie) => (
              <div key={movie.id} className="movie-box">
                <Link to="/movie/">
                  <img
                    className="movie-box-img"
                    src={
                      "https://image.tmdb.org/t/p/original" + movie.poster_path
                    }
                    alt={"s"}
                  />
                  <div className="movie-infos">
                    <h2 className="movie-title">{movie.title}</h2>
                    <div className="movie-vote">
                      <span className="imdb">IMDb</span>
                      <span className="vote">{movie.vote_average}</span>
                    </div>
                    <div className="movie-overview">
                      <p>{movie.overview}</p>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        </section>
      ) : null}
    </>
  );
}

export default MoviesBoxes;
