import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Table = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const response = await axios.get('https://api.themoviedb.org/3/movie/popular?api_key=977df55b389183b892b6c11af75af168');
      setMovies(response.data.results);
    };

    fetchMovies();
  }, []);

  return (
    <div>
      <h1>Movie Table</h1>
      <table style={{color: 'white'}}>
        <thead>
          <tr>
            <th>Backdop</th>
            <th>Title</th>
            <th>Title</th>
            <th>Release Date</th>
            <th>Vote Average</th>
            <th>Overview</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td><img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={`${movie.title} poster`} /></td>
              <td>{movie.title}</td>
              <td>{movie.title}</td>
              <td>{movie.release_date}</td>
              <td>{movie.vote_average}</td>
              <td>{movie.overview}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
