import React from "react";
import { useState } from "react";
import MoviesBoxes from "../movies-boxes/MoviesBoxes";
import "./movies.scss";

function Movies() {
  const [movieType, setMovieType] = useState({});

  const movieTypes = [
    {
      title: "Popular",
      endpoint: "popular",
    },
    {
      title: "Top Rated",
      endpoint: "top-rated",
    },
    {
      title: "Upcoming",
      endpoint: "upcoming",
    },
  ];

  const change = (type) => {
    setMovieType(type);
  };
  return (
    <div className="all-movies">
        <br/>
        <br/>
        <br/>
        <br/>
      {movieTypes.map((type) => (
        <button type="button" onClick={() => change(type)}>
          {type.title}
        </button>
      ))}

      <MoviesBoxes title={movieType.title} endpoint={movieType.endpoint} />
    </div>
  );
}

export default Movies;
